#ifndef __IO_SERVICE_H__
#define __IO_SERVICE_H__

#include <utils/Singleton.h>
#include "TimedJob.h"

class IOService: public TimedJob, public android::Singleton<IOService> {
private:
    enum IOType {
        IO_MMC = 0,
        IO_SCSI = 1,
    };
    int mIOType;
public:
    IOService() {};
    virtual ~IOService() {};
    /* boost IO frequency */
    int boost(int timeoutMs);
protected:
    virtual int handleTimeout(int32_t *data, int size);
};

#endif
